import React, {Component} from "react";

class App extends Component {

  constructor() {
    super();
    this.state={
      task: {text: ''},
      tasks: [],
    };

  }

  handlechange = (e) =>{
    this.setState({
      task: {
        text: e.target.value,
      }
    })
  }

  onSubmitTask = (e) =>{
    e.preventDefault();
    this.setState({
      tasks: this.state.tasks.concat(this.state.task),
      task: {text: ''},
    });
  }

   render(){
     const {task, tasks} = this.state;  /* {Detructuring for cleaner code} */

     return (
        <div>
          <form>
            <label htmlFor="taskInput">Enter task</label>
            <input type="text" id="taskInput"/>
            <button type="submit">Add Task</button>
          </form>
        </div>
     );
   }
  }

export default App;
